from html.parser import HTMLParser
# from urllib.request import urlopen
import re
from read_site_content import read_site_content

class HabrPageListTaker(HTMLParser):
# Проходим по странице Хабра и возвращаем id статей и их тип (блог или статья)

    result = []
    next_page = ''
    def handle_starttag(self, tag, attrs):

        if tag == 'a':
            for attr in attrs:
                if attr[1] == 'post__title_link' :
                    strr = attrs[0][1]
                    id_and_type = re.search('/\w+/\d+/', strr)
                    if id_and_type:
                        id_and_types = id_and_type.group(0).split('/')[1:3]
                        if id_and_types[0] == 'blog':
                            company = strr[29:].split('/')[0]
                            id_and_types.append(company)
                        self.result.append(id_and_types)


                if attr[1] == 'next_page':
                    self.next_page = 'https://habrahabr.ru' + attrs [2][1]

# def read_site_content(url):
#     site_dirty_content =  str(urlopen(url).read().decode())
#     # очистка результата от \n и пробелов
#     return re.sub(r'\\' + 'n+\s*', '', site_dirty_content)

def take_all_pages(start_page):
    print(start_page)
    # result = []
    habr_page = read_site_content(start_page)
    parser = HabrPageListTaker()
    parser.feed(habr_page)
    # result.append(parser.result)
    if parser.next_page:
        take_all_pages(parser.next_page)
    return parser.result


